import React, { useState, useEffect, useRef, useCallback } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import {
  Box,
  TextField,
  Button,
  Paper,
  Typography,
  IconButton,
  List,
  ListItem,
  Divider,
  Switch,
  FormControlLabel,
} from '@mui/material';
import { Send as SendIcon, Share as ShareIcon } from '@mui/icons-material';
import axios from 'axios';
import 'katex/dist/katex.min.css';

interface Message {
  content: string;
  role: 'user' | 'assistant';
}

const API_BASE_URL = 'http://localhost:8001';

const ChatInterface: React.FC = () => {
  const { chatId } = useParams<{ chatId: string }>();
  const navigate = useNavigate();
  const [messages, setMessages] = useState<Message[]>([]);
  const [input, setInput] = useState('');
  const [isPublic, setIsPublic] = useState(false);
  const messagesEndRef = useRef<HTMLDivElement>(null);

  const createNewChat = useCallback(async () => {
    try {
      const response = await axios.post(`${API_BASE_URL}/chats`, { is_public: isPublic });
      navigate(`/chat/${response.data.chat_id}`);
    } catch (error) {
      console.error('Error creating chat:', error);
    }
  }, [isPublic, navigate]);

  const fetchChatHistory = useCallback(async () => {
    if (!chatId) return;
    try {
      const response = await axios.get(`${API_BASE_URL}/chats/${chatId}`);
      setMessages(response.data.messages);
    } catch (error) {
      console.error('Error fetching chat history:', error);
    }
  }, [chatId]);

  useEffect(() => {
    if (chatId) {
      fetchChatHistory();
    } else {
      createNewChat();
    }
  }, [chatId, createNewChat, fetchChatHistory]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSend = async () => {
    if (!input.trim() || !chatId) return;

    const userMessage: Message = { content: input, role: 'user' };
    setMessages(prev => [...prev, userMessage]);
    setInput('');

    try {
      // First, save the user message
      await axios.post(`${API_BASE_URL}/chats/${chatId}/messages`, userMessage);

      // Then, search for papers and generate response
      const searchResponse = await axios.get(`${API_BASE_URL}/search`, {
        params: { query: input }
      });

      const papers = searchResponse.data.papers;
      const assistantMessage: Message = {
        content: `Here are some relevant papers:\n\n${papers.map((paper: any) => 
          `- ${paper.title}\n  Authors: ${paper.authors.join(', ')}\n  Summary: ${paper.summary}\n`
        ).join('\n')}`,
        role: 'assistant'
      };

      setMessages(prev => [...prev, assistantMessage]);
      await axios.post(`${API_BASE_URL}/chats/${chatId}/messages`, assistantMessage);
    } catch (error) {
      console.error('Error sending message:', error);
    }
  };

  const handleShare = async () => {
    if (!chatId) return;
    try {
      await axios.post(`${API_BASE_URL}/chats/${chatId}`, { is_public: true });
      setIsPublic(true);
      // You can add a share URL copy functionality here
    } catch (error) {
      console.error('Error sharing chat:', error);
    }
  };

  const renderMessage = (message: Message) => {
    const isUser = message.role === 'user';
    return (
      <ListItem
        sx={{
          display: 'flex',
          justifyContent: isUser ? 'flex-end' : 'flex-start',
          mb: 2,
        }}
      >
        <Paper
          elevation={1}
          sx={{
            p: 2,
            maxWidth: '70%',
            bgcolor: isUser ? 'primary.main' : 'grey.100',
            color: isUser ? 'white' : 'text.primary',
          }}
        >
          <Typography variant="body1">
            {message.content.split('\n').map((line, i) => (
              <React.Fragment key={i}>
                {line}
                <br />
              </React.Fragment>
            ))}
          </Typography>
        </Paper>
      </ListItem>
    );
  };

  return (
    <Box sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
      <Box sx={{ mb: 2, display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <Typography variant="h4">ML Paper Search</Typography>
        <Box>
          <FormControlLabel
            control={
              <Switch
                checked={isPublic}
                onChange={(e) => setIsPublic(e.target.checked)}
                disabled={!!chatId}
              />
            }
            label="Public Chat"
          />
          {chatId && (
            <IconButton onClick={handleShare} color="primary">
              <ShareIcon />
            </IconButton>
          )}
        </Box>
      </Box>

      <Paper
        elevation={3}
        sx={{
          flex: 1,
          overflow: 'auto',
          mb: 2,
          p: 2,
          bgcolor: 'background.paper',
        }}
      >
        <List>
          {messages.map((message, index) => (
            <React.Fragment key={index}>
              {renderMessage(message)}
              {index < messages.length - 1 && <Divider />}
            </React.Fragment>
          ))}
          <div ref={messagesEndRef} />
        </List>
      </Paper>

      <Box sx={{ display: 'flex', gap: 1 }}>
        <TextField
          fullWidth
          variant="outlined"
          placeholder="Ask about ML papers..."
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyPress={(e) => e.key === 'Enter' && handleSend()}
        />
        <Button
          variant="contained"
          color="primary"
          onClick={handleSend}
          disabled={!input.trim()}
        >
          <SendIcon />
        </Button>
      </Box>
    </Box>
  );
};

export default ChatInterface;
